import React from 'react';
import data from './data.json';
import Products from './components/Products';
import Filter from './components/Filter';
import Cart from './components/Cart';
import './index.css';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      products: data.products,
      cartItems: [],
      size: '', //filter thru all sizes
      sort: '', //sort thru $ and $$$
    };
  }
  //function used to handle onchange event for sorting
  //CONVERTING TO CHANGE FUNC TO METHOD FUNC WE CAN ACCESS THIS.SET-STATE
  sortProducts = (event) => {
    const sort = event.target.value;
    console.log(event.target.value);
    this.setState((state) => ({
      sort: sort,
      products: this.state.products
        .slice()
        .sort((a, b) =>
          sort === 'lowest'
            ? a.price > b.price
              ? 1
              : -1
            : sort === 'highest'
            ? a.price < b.price
              ? 1
              : -1
            : a._id < b._id
            ? 1
            : -1
        ),
    }));
  };
  //function handling onchange event thru filtering thru the sizes
  //CONVERTING TO CHANGE FUNC TO METHOD FUNC WE CAN ACCESS THIS.SET-STATE
  filterProducts = (event) => {
     if (event.target.value === ''){
       this.setState({
         size: event.target.value,
         product: data.products
       })
     }else{
      this.setState({
        //if there is a value in the array
        size: event.target.value,
        products: data.products.filter((product) => product.availableSizes.indexOf(event.target.value) >= 0
        )
      })
    }
  }


  filterProducts = (event) => {
    //THE VALUE OF SIZE IS event.target.value
    console.log(event.target.value);
    if (event.target.value === '') {
      this.setState({
      size: event.target.value,
      products: data.products
    });
    } else {
      this.setState({
        //THE VALUE OF SIZE IS event.target.value
        size: event.target.value,
        //for products using filter method
        //checking for size! if > 0 => the size exists
        products: data.products.filter(
          (product) => product.availableSizes.indexOf(event.target.value) >= 0
        ),
      });
    }
  };

  removeFromCart = (product) => {
    const cartItems = this.state.cartItems.slice();
    this.setState({
      cartItems: cartItems.filter((x) => x._id !== product._id),
    });
  };
  addToCart = (product) => {
    const cartItems = this.state.cartItems.slice();
    let alreadyInCart = false;
    cartItems.forEach((item) => {
      if (item._id === product._id) {
        item.count++;
        alreadyInCart = true;
      }
    });
    if (!alreadyInCart) {
      cartItems.push({ ...product, count: 1 });
    }
    this.setState({ cartItems });
  };

  render() {
    return (
      <div className='grid-container'>
        <header>
          <a href='/'>FunWear🌼Fashion</a>
        </header>
        <main>
          <div className='content'>
            <div className='main'>
              <Filter
                //passing count & sort & size as props to Filter
                count={this.state.products.length}
                sort={this.state.sort}
                size={this.state.size}
                // functions handling onChange events
                sortProducts={this.sortProducts}
                filterProducts={this.filterProducts}
              ></Filter>
              <Products
                products={this.state.products}
                addToCart={this.addToCart}
              ></Products>
            </div>
            <div className='sidebar'>
              <Cart
                cartItems={this.state.cartItems}
                removeFromCart={this.removeFromCart}
              />
            </div>
          </div>
        </main>
        <footer>© All right is reserved.</footer>
      </div>
    );
  }
}

export default App;
