import {createStore, applyMiddleware, compose, combineReducers} from 'redux';
import thunk from "redux-thunk"

import {productsReducer} from "./reducers/productReducers"; //it needs to be in {} bc/ it is not a default export it is a NAME EXPORT

const initialState = {};//define initial state
const composeEnhancer = window._REDUX_DEVTOOLS_EXTENSION_COMPOSE_ || compose;

//store accepts multiple params
//we combine all reducers together
const store = createStore(
  combineReducers({
    products: productsReducer,
  }),
  initialState,
  composeEnhancer(applyMiddleware(thunk))
)

export default store;
